import { useState, useEffect } from 'react';
import axios from 'axios';
import {
  RefreshCw, FolderPlus, CheckCircle, AlertCircle,
  ArrowRight, Layers, Image as ImageIcon, Database
} from 'lucide-react';
import './App.css';

// Backend Portu (5001 olarak ayarlamıştık)
const API_BASE_URL = 'http://127.0.0.1:5001/api';

function App() {
  // Varsayılan Repo ID (Bulduğun gerçek ID'yi buraya yazabilirsin, örn: 15)
  const [repoId, setRepoId] = useState(1);

  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');

  const [loading, setLoading] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [error, setError] = useState(null);

  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  // Klasörleri Çek
  const fetchFolders = async () => {
    if (!repoId) return;
    try {
      const response = await axios.get(`${API_BASE_URL}/folders/${repoId}`);
      const data = response.data.folders || [];
      setFolders(Array.isArray(data) ? data : []);

      // Eğer daha önce seçili bir klasör varsa ve listede hala mevcutsa koru, yoksa sıfırla
      if (selectedFolder && !data.find(f => f.id === parseInt(selectedFolder))) {
        setSelectedFolder('');
      }
    } catch (err) {
      console.error("Klasör hatası:", err);
      setFolders([]);
    }
  };

  useEffect(() => {
    fetchFolders();
  }, [repoId]);

  // Senkronizasyon
  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) {
      setError("Lütfen Jira Key ve Hedef Klasör seçiniz.");
      return;
    }

    setLoading(true);
    setError(null);
    setSyncResult(null);

    try {
      const payload = {
        jira_input: jiraInput,
        folder_id: parseInt(selectedFolder),
        project_id: parseInt(repoId) // Backend bunu repo_id olarak kullanacak
      };

      const response = await axios.post(`${API_BASE_URL}/sync`, payload);
      setSyncResult(response.data);
      setJiraInput(''); // Başarılıysa temizle
    } catch (err) {
      console.error("Sync hatası:", err);
      const msg = err.response?.data?.error || err.message;
      setError("İşlem Başarısız: " + msg);
    } finally {
      setLoading(false);
    }
  };

  // Yeni Klasör
  const handleCreateFolder = async () => {
    if (!newFolderName) return;
    try {
      const payload = {
        name: newFolderName,
        parent_id: selectedFolder ? parseInt(selectedFolder) : null
      };
      // Klasörü oluştur
      const res = await axios.post(`${API_BASE_URL}/folders/${repoId}`, payload);

      // Listeyi yenile
      await fetchFolders();

      // Yeni klasörü otomatik seç (ID döndüyse)
      if (res.data && res.data.id) {
        setSelectedFolder(res.data.id);
      }

      setNewFolderName('');
      setShowNewFolder(false);
    } catch (err) {
      setError("Klasör oluşturulamadı: " + err.message);
    }
  };

  return (
    <div className="app-container">
      <div className="main-wrapper">

        {/* Header */}
        <div className="header-card">
          <div className="header-left">
            <div className="logo-box">
              <RefreshCw size={24} />
            </div>
            <div className="header-info">
              <h1>Jira ↔ Testmo Sync</h1>
              <p>Test senaryolarını ve görselleri otomatik aktarın</p>
            </div>
          </div>
          <div className="mode-badge">
            <Database size={16}/>
            <span>Mode: Repository</span>
          </div>
        </div>

        <div className="grid-layout">

          {/* SOL PANEL */}
          <div className="sidebar">

            {/* Repo ID */}
            <div className="card">
              <label>Repository ID</label>
              <input
                type="number"
                value={repoId}
                onChange={(e) => setRepoId(e.target.value)}
                className="form-input"
                placeholder="Örn: 15"
              />
              <p className="helper-text">Testmo URL'indeki repositories/<b>ID</b> kısmını giriniz.</p>
            </div>

            {/* Klasör Seçimi */}
            <div className="card">
              <div style={{display:'flex', justifyContent:'space-between', marginBottom:'8px'}}>
                <label style={{margin:0}}>Hedef Klasör</label>
                <button onClick={() => setShowNewFolder(!showNewFolder)} className="btn-link">
                  <FolderPlus size={14}/> {showNewFolder ? 'İptal' : 'Yeni'}
                </button>
              </div>

              {showNewFolder && (
                <div className="new-folder-area">
                  <input
                    type="text"
                    placeholder="Klasör Adı..."
                    value={newFolderName}
                    onChange={(e) => setNewFolderName(e.target.value)}
                    className="form-input"
                    style={{marginBottom:'8px'}}
                  />
                  <button onClick={handleCreateFolder} className="btn btn-primary btn-sm">
                    Oluştur
                  </button>
                </div>
              )}

              <select
                value={selectedFolder}
                onChange={(e) => setSelectedFolder(e.target.value)}
                className="form-select"
                disabled={folders.length === 0}
              >
                <option value="">
                  {folders.length === 0 ? 'Klasörler yükleniyor...' : 'Klasör Seçin...'}
                </option>
                {folders.map(f => (
                  <option key={f.id} value={f.id}>{f.name}</option>
                ))}
              </select>
            </div>
          </div>

          {/* SAĞ PANEL */}
          <div className="content-area">

            <div className="card">
              <label>Jira Task Linki veya Key</label>
              <div style={{display:'flex', gap:'10px'}}>
                <input
                  type="text"
                  placeholder="Örn: PROJ-123"
                  value={jiraInput}
                  onChange={(e) => setJiraInput(e.target.value)}
                  className="form-input"
                  style={{fontSize:'1.1rem'}}
                />
                <button
                  onClick={handleSync}
                  disabled={loading}
                  className="btn btn-primary"
                  style={{minWidth:'160px'}}
                >
                  {loading ? <RefreshCw className="spin" size={20}/> : <>Senkronize Et <ArrowRight size={20}/></>}
                </button>
              </div>
              <p className="helper-text">* Açıklama ve yorumlardaki TC'ler görsellerle birlikte çekilecektir.</p>
            </div>

            {error && (
              <div className="error-box">
                <AlertCircle size={24} />
                <div>
                  <strong style={{display:'block', marginBottom:'4px'}}>Hata Oluştu</strong>
                  <span style={{fontSize:'0.9rem'}}>{error}</span>
                </div>
              </div>
            )}

            {syncResult && (
              <div className="result-card">
                <div className="result-header">
                  <div className="success-icon"><CheckCircle size={24}/></div>
                  <div>
                    <h3 style={{margin:0, color:'#166534'}}>Senkronizasyon Başarılı!</h3>
                    <div className="result-stats">
                      <span className="stat-pill">Task: {syncResult.taskKey}</span>
                      <span className="stat-pill">Case: {syncResult.testCasesCreated}</span>
                      <span className="stat-pill">Görsel: {syncResult.imagesUploaded}</span>
                    </div>
                  </div>
                </div>

                <div className="result-list">
                  <label style={{color:'#64748b', textTransform:'uppercase', fontSize:'0.75rem', letterSpacing:'0.05em'}}>
                    <Layers size={14} style={{verticalAlign:'middle', marginRight:'5px'}}/>
                    Aktarılan Senaryolar
                  </label>

                  <div style={{marginTop:'10px'}}>
                    {syncResult.cases.map((tc, idx) => (
                      <div key={idx} className="case-item">
                        <span className="case-name">{idx + 1}. {tc.name}</span>
                        <div>
                          <span className="tag tag-created">{tc.status}</span>
                          {tc.images > 0 && (
                            <span className="tag tag-img"><ImageIcon size={12} style={{verticalAlign:'middle'}}/> {tc.images}</span>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}

          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
