import { useState, useEffect } from 'react';
import axios from 'axios';
import { RefreshCw, FolderPlus, CheckCircle, XCircle, ArrowRight, Server, Layers } from 'lucide-react';
import './App.css'; // CSS dosyasÄ±nÄ±n import edildiÄŸinden emin ol

// Backend URL'i
const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  // State YÃ¶netimi
  const [projectId, setProjectId] = useState(1); // VarsayÄ±lan Project ID
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');

  // UI DurumlarÄ±
  const [loading, setLoading] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [error, setError] = useState(null);

  // Yeni KlasÃ¶r State'i
  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  // KlasÃ¶rleri Getir Fonksiyonu
  const fetchFolders = async () => {
    // EÄŸer ID boÅŸsa veya geÃ§ersizse listeyi ve seÃ§imi temizle
    if (!projectId) {
      setFolders([]);
      setSelectedFolder('');
      return;
    }

    try {
      const response = await axios.get(`${API_BASE_URL}/folders/${projectId}`);

      // API yapÄ±sÄ±na gÃ¶re data kontrolÃ¼
      const data = response.data.folders || response.data;
      setFolders(Array.isArray(data) ? data : []);

      // Ã–NEMLÄ°: Proje deÄŸiÅŸtiÄŸi iÃ§in seÃ§ili klasÃ¶rÃ¼ sÄ±fÄ±rla
      // BÃ¶ylece kullanÄ±cÄ± yanlÄ±ÅŸlÄ±kla eski projenin klasÃ¶r ID'sini gÃ¶nderemez
      setSelectedFolder('');

    } catch (err) {
      console.error("KlasÃ¶rler alÄ±namadÄ±:", err);
      // Hata durumunda listeyi temizle
      setFolders([]);
      setSelectedFolder('');
    }
  };

  // Project ID her deÄŸiÅŸtiÄŸinde klasÃ¶rleri yeniden Ã§ek
  useEffect(() => {
    fetchFolders();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [projectId]);

  // Senkronizasyon Ä°ÅŸlemi
  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) {
      setError("LÃ¼tfen Jira Linki ve Hedef KlasÃ¶r seÃ§iniz.");
      return;
    }

    setLoading(true);
    setError(null);
    setSyncResult(null);

    try {
      const payload = {
        jira_input: jiraInput,
        folder_id: parseInt(selectedFolder),
        project_id: parseInt(projectId)
      };

      const response = await axios.post(`${API_BASE_URL}/sync`, payload);
      setSyncResult(response.data);
      setJiraInput(''); // BaÅŸarÄ±lÄ±ysa inputu temizle
    } catch (err) {
      setError(err.response?.data?.error || "Senkronizasyon sÄ±rasÄ±nda bir hata oluÅŸtu.");
    } finally {
      setLoading(false);
    }
  };

  // Yeni KlasÃ¶r OluÅŸturma
  const handleCreateFolder = async () => {
    if (!newFolderName) return;

    try {
      const response = await axios.post(`${API_BASE_URL}/folders/${projectId}`, {
        name: newFolderName,
        parent_id: selectedFolder ? parseInt(selectedFolder) : null // SeÃ§iliyse onun altÄ±na, deÄŸilse root
      });

      await fetchFolders(); // Listeyi gÃ¼ncelle (seÃ§imi korumak iÃ§in setSelectedFolder burada sÄ±fÄ±rlanmazsa iyi olur ama fetchFolders sÄ±fÄ±rlÄ±yor)

      setNewFolderName('');
      setShowNewFolder(false);

      // Yeni oluÅŸturulan klasÃ¶rÃ¼ otomatik seÃ§
      if(response.data.id) {
        // fetchFolders asenkron Ã§alÄ±ÅŸtÄ±ÄŸÄ± iÃ§in burada manuel set etmek gerekebilir
        // Ancak fetchFolders iÃ§indeki setSelectedFolder('') bunu ezebilir.
        // Pratik Ã§Ã¶zÃ¼m: fetchFolders Ã§alÄ±ÅŸtÄ±ktan sonra ID'yi set etmek.
        // Åžimdilik basit bÄ±rakalÄ±m, kullanÄ±cÄ± listeden gÃ¶recektir.
        // Ä°stersen buraya Ã¶zel bir logic ekleyebiliriz.
