import { useState, useEffect } from 'react';
import axios from 'axios';
import { RefreshCw, FolderPlus, CheckCircle, XCircle, ArrowRight, Server, Layers } from 'lucide-react';
import './App.css';

// localhost yerine 127.0.0.1 kullanarak baƒülantƒ± sorununu √ß√∂zeriz
const API_BASE_URL = 'http://127.0.0.1:5000/api';

function App() {
  const [projectId, setProjectId] = useState(1);
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');

  const [loading, setLoading] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [error, setError] = useState(null);

  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  const fetchFolders = async () => {
    if (!projectId) {
      setFolders([]);
      setSelectedFolder('');
      return;
    }

    try {
      console.log(`Fetching folders from: ${API_BASE_URL}/folders/${projectId}`);
      const response = await axios.get(`${API_BASE_URL}/folders/${projectId}`);

      // Backend { folders: [...] } d√∂n√ºyor
      const data = response.data.folders || [];
      setFolders(Array.isArray(data) ? data : []);
      setSelectedFolder(''); 

    } catch (err) {
      console.error("Klas√∂r hatasƒ±:", err);
      setFolders([]);
    }
  };

  useEffect(() => {
    fetchFolders();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [projectId]);

  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) {
      setError("L√ºtfen Jira Linki ve Hedef Klas√∂r se√ßiniz.");
      return;
    }

    setLoading(true);
    setError(null);
    setSyncResult(null);

    try {
      const payload = {
        jira_input: jiraInput,
        folder_id: parseInt(selectedFolder),
        project_id: parseInt(projectId)
      };

      const response = await axios.post(`${API_BASE_URL}/sync`, payload);
      setSyncResult(response.data);
      setJiraInput('');
    } catch (err) {
      console.error("Sync hatasƒ±:", err);
      setError(err.response?.data?.error || "Sunucuya baƒülanƒ±lamadƒ±.");
    } finally {
      setLoading(false);
    }
  };

  const handleCreateFolder = async () => {
    if (!newFolderName) return;

    try {
      const payload = {
        name: newFolderName,
        parent_id: selectedFolder ? parseInt(selectedFolder) : null
      };

      const response = await axios.post(`${API_BASE_URL}/folders/${projectId}`, payload);

      // Listeyi yenile
      await fetchFolders();

      setNewFolderName('');
      setShowNewFolder(false);

      // Yeni olu≈üturulan klas√∂r√º ID ile bulup se√ßmeye √ßalƒ±≈üabiliriz
      // Ancak fetchFolders resetlediƒüi i√ßin kullanƒ±cƒ± listeden g√∂recektir.

    } catch (err) {
      console.error("Olu≈üturma hatasƒ±:", err);
      setError("Klas√∂r olu≈üturulamadƒ±: " + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="app-container">
      <div className="main-wrapper">

        <div className="header-card">
          <div className="header-left">
            <div className="icon-box bg-primary">
              <RefreshCw color="white" size={24} />
            </div>
            <div>
              <h1>Jira ‚Üî Testmo Sync</h1>
              <p>Test senaryolarƒ±nƒ± ve g√∂rselleri otomatik aktarƒ±n</p>
            </div>
          </div>
          <div className="status-badge">
            <Server size={16}/>
            <span>API: Connected</span>
          </div>
        </div>

        <div className="grid-layout">
          <div className="sidebar">
            <div className="card">
              <label>Project ID</label>
              <input 
                type="number" 
                value={projectId}
                onChange={(e) => setProjectId(e.target.value)}
                className="form-input"
                min="1"
              />
            </div>

            <div className="card">
              <div className="label-row">
                <label>Hedef Klas√∂r</label>
                <button 
                  onClick={() => setShowNewFolder(!showNewFolder)}
                  className="btn-link"
                >
                  <FolderPlus size={14}/> Yeni
                </button>
              </div>

              {showNewFolder && (
                <div className="new-folder-box">
                  <input 
                    type="text" 
                    placeholder="Klas√∂r Adƒ±"
                    value={newFolderName}
                    onChange={(e) => setNewFolderName(e.target.value)}
                    className="form-input small-input"
                  />
                  <button onClick={handleCreateFolder} className="btn btn-primary btn-sm width-100">
                    Olu≈ütur
                  </button>
                </div>
              )}

              <select 
                value={selectedFolder}
                onChange={(e) => setSelectedFolder(e.target.value)}
                className="form-select"
                disabled={folders.length === 0}
              >
                <option value="">
                  {folders.length === 0 ? 'Klas√∂r yok veya y√ºkleniyor...' : 'Klas√∂r Se√ßin...'}
                </option>
                {folders.map(f => (
                  <option key={f.id} value={f.id}>{f.name}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="content-area">
            <div className="card">
