import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast'; // Toast kütüphanesi
import {
  Zap, FolderPlus, Check, ArrowRight,
  FileText, Image as ImageIcon, Database, Loader2
} from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://127.0.0.1:5001/api';

function App() {
  useEffect(() => {
    document.title = "QuickCase | Enterprise Sync";
  }, []);

  const [repoId, setRepoId] = useState(1);
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');

  const [loading, setLoading] = useState(false);
  const [foldersLoading, setFoldersLoading] = useState(false); // Klasör yükleniyor durumu
  const [syncResult, setSyncResult] = useState(null);

  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  // Klasörleri Çek
  const fetchFolders = useCallback(async () => {
    if (!repoId) return;
    setFoldersLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/folders/${repoId}`);
      const data = response.data.folders || [];
      setFolders(Array.isArray(data) ? data : []);

      if (selectedFolder && !data.find(f => f.id === parseInt(selectedFolder))) {
        setSelectedFolder('');
      }
    } catch (err) {
      console.error(err);
      // Sessiz hata, toast ile kullanıcıyı boğmayalım ama console'a yazalım
    } finally {
      setFoldersLoading(false);
    }
  }, [repoId, selectedFolder]);

  useEffect(() => {
    fetchFolders();
  }, [fetchFolders]);

  // Senkronizasyon
  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) {
      toast.error("Lütfen Jira Task ID ve Hedef Klasör seçiniz.");
      return;
    }

    setLoading(true);
    setSyncResult(null);

    // Yükleniyor toast'ı başlat
    const toastId = toast.loading('Jira verileri çekiliyor ve işleniyor...');

    try {
      const payload = {
        jira_input: jiraInput,
        folder_id: parseInt(selectedFolder),
        project_id: parseInt(repoId)
      };

      const response = await axios.post(`${API_BASE_URL}/sync`, payload);
      setSyncResult(response.data);
      setJiraInput('');

      // Başarılı Toast
      toast.success(`Başarılı! ${response.data.testCasesCreated} case oluşturuldu.`, {
        id: toastId,
        duration: 5000,
      });

    } catch (err) {
      const msg = err.response?.data?.error || "Sunucu hatası oluştu.";
      // Hata Toast
      toast.error(msg, { id: toastId });
    } finally {
      setLoading(false);
    }
  };

  // Yeni Klasör
  const handleCreateFolder = async () => {
    if (!newFolderName) return;

    const toastId = toast.loading('Klasör oluşturuluyor...');

    try {
      const payload = {
        name: newFolderName,
        parent_id: selectedFolder ? parseInt(selectedFolder) : null
      };
      const res = await axios.post(`${API_BASE_URL}/folders/${repoId}`, payload);

      await fetchFolders();
      if (res.data && res.data.id) setSelectedFolder(res.data.id);

      setNewFolderName('');
      setShowNewFolder(false);
      toast.success('Klasör oluşturuldu!', { id: toastId });

    } catch (err) {
      toast.error("Klasör oluşturulamadı: " + err.message, { id: toastId });
    }
  };

  return (
    <div className="app-container">
      {/* Toast Bildirim Konumu */}
      <Toaster position="top-center" reverseOrder={false} />

      <div className="main-wrapper">

        {/* HEADER */}
        <div className="header-card">
          <div className="header-brand">
            <div className="logo-box">
              <Zap size={28} strokeWidth={2.5} />
            </div>
            <div className="brand-text">
              <h1>QuickCase</h1>
              <p>Enterprise Test Senaryo Yönetimi</p>
            </div>
          </div>
          <div className="status-badge">
            <Database size={16}/>
            <span>Repo Mode: Active</span>
          </div>
        </div>

        <div className="grid-layout">

          {/* SIDEBAR (Ayarlar) */}
          <div className="sidebar">

            <div className="card">
              <div className="form-group">
                <label className="form-label">Repository ID</label>
                <input
                  type="number"
                  value={repoId}
                  onChange={(e) => setRepoId(e.target.value)}
                  className="form-input"
                  placeholder="Örn: 15"
                />
                <p className="helper-text">Testmo URL'indeki ID (repositories/<b>ID</b>)</p>
              </div>
            </div>

            <div className="card">
              <div className="form-group">
                <label className="form-label">
                  Hedef Klasör
                  <button onClick={() => setShowNewFolder(!showNewFolder)} className="btn btn-text">
                    <FolderPlus size={16} style={{marginRight:4}}/>
                    {showNewFolder ? 'İptal' : 'Yeni Oluştur'}
                  </button>
                </label>

                {showNewFolder && (
                  <div className="new-folder-wrapper">
                    <input
                      type="text"
                      placeholder="Klasör Adı..."
                      value={newFolderName}
                      onChange={(e) => setNewFolderName(e.target.value)}
                      className="form-input"
                    />
                    <button onClick={handleCreateFolder} className="btn btn-small-action">
                      Onayla ve Oluştur
                    </button>
                  </div>
                )}

                <div className="input-container">
                  <select
                    value={selectedFolder}
                    onChange={(e) => setSelectedFolder(e.target.value)}
                    className="form-select"
                    disabled={foldersLoading || folders.length === 0}
                  >
                    <option value="">
                      {foldersLoading ? 'Klasörler yükleniyor...' : 'Bir klasör seçin...'}
                    </option>
                    {folders.map(f => (
                      <option key={f.id} value={f.id}>{f.name}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          </div>

          {/* CONTENT AREA (İşlemler) */}
          <div className="content-area">

            <div className="card">
              <label className="form-label" style={{fontSize:'1.1rem'}}>Jira Task Entegrasyonu</label>
              <div style={{display:'flex', gap:'12px', marginTop:'1rem'}}>
                <div style={{flex:1}}>
                  <input
                    type="text"
                    placeholder="Örn: PROJ-123"
                    value={jiraInput}
                    onChange={(e) => setJiraInput(e.target.value)}
                    className="form-input"
                    style={{padding:'1rem', fontSize:'1.1rem', fontWeight:'600'}}
                  />
                </div>
                <button
                  onClick={handleSync}
                  disabled={loading}
                  className="btn btn-primary"
                  style={{width:'200px'}}
                >
                  {loading ? (
                    <><Loader2 className="spinner" size={20} /> İşleniyor...</>
                  ) : (
                    <>Quick Sync <ArrowRight size={20} /></>
                  )}
                </button>
              </div>
              <p className="helper-text" style={{marginTop:'1rem'}}>
                * Task açıklaması, yorumlardaki TC'ler ve tüm görseller (Figma, Ekler, Gömülü) otomatik işlenir.
              </p>
            </div>

            {/* SONUÇ KARTI */}
            {syncResult && (
              <div className="result-card">
                <div className="result-header">
                  <div className="success-icon-box">
                    <Check size={28} strokeWidth={3} />
                  </div>
                  <div>
                    <h3 className="result-title">Senkronizasyon Tamamlandı!</h3>
                    <div className="result-meta">
                      <span className="meta-tag">Task: {syncResult.taskKey}</span>
                      <span className="meta-tag">Case: {syncResult.testCasesCreated}</span>
                      <span className="meta-tag">Görsel: {syncResult.imagesUploaded}</span>
                    </div>
                  </div>
                </div>

                <div className="case-list">
                  {syncResult.cases.map((tc, idx) => (
                    <div key={idx} className="case-item">
                      <div className="case-info">
                        <FileText size={20} className="text-gray-400"/>
                        <span className="case-name">{tc.name}</span>
                      </div>
                      <div style={{display:'flex', alignItems:'center', gap:'8px'}}>
                        <span className="status-badge-success">CREATED</span>
                        {tc.images > 0 && (
                          <span className="img-badge">
                            <ImageIcon size={14}/> {tc.images}
                          </span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
