import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import {
  Zap, FolderPlus, Check, AlertOctagon,
  ArrowRight, FileText, Image as ImageIcon, Database
} from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://127.0.0.1:5001/api';

function App() {
  // Tarayıcı başlığını güncelle
  useEffect(() => {
    document.title = "QuickCase | Jira to Testmo";
  }, []);

  const [repoId, setRepoId] = useState(1);
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');

  const [loading, setLoading] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [error, setError] = useState(null);

  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  const fetchFolders = useCallback(async () => {
    if (!repoId) return;
    try {
      const response = await axios.get(`${API_BASE_URL}/folders/${repoId}`);
      const data = response.data.folders || [];
      setFolders(Array.isArray(data) ? data : []);

      if (selectedFolder && !data.find(f => f.id === parseInt(selectedFolder))) {
        setSelectedFolder('');
      }
    } catch (err) {
      console.error("Klasör hatası:", err);
      setFolders([]);
    }
  }, [repoId, selectedFolder]);

  useEffect(() => {
    fetchFolders();
  }, [fetchFolders]);

  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) {
      setError("Lütfen Jira Key ve Hedef Klasör seçiniz.");
      return;
    }

    setLoading(true);
    setError(null);
    setSyncResult(null);

    try {
      const payload = {
        jira_input: jiraInput,
        folder_id: parseInt(selectedFolder),
        project_id: parseInt(repoId)
      };

      const response = await axios.post(`${API_BASE_URL}/sync`, payload);
      setSyncResult(response.data);
      setJiraInput('');
    } catch (err) {
      const msg = err.response?.data?.error || err.message;
      setError("İşlem Başarısız: " + msg);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateFolder = async () => {
    if (!newFolderName) return;
    try {
      const payload = {
        name: newFolderName,
        parent_id: selectedFolder ? parseInt(selectedFolder) : null
      };
      const res = await axios.post(`${API_BASE_URL}/folders/${repoId}`, payload);
      await fetchFolders();
      if (res.data && res.data.id) setSelectedFolder(res.data.id);
      setNewFolderName('');
      setShowNewFolder(false);
    } catch (err) {
      setError("Klasör oluşturulamadı: " + err.message);
    }
  };

  return (
    <div className="app-container">
      <div className="main-wrapper">

        {/* Header & Logo */}
        <div className="header-card">
          <div className="header-left">
            <div className="logo-container">
              <Zap size={28} color="white" fill="white" />
            </div>
            <div className="logo-text">
              <h1>QuickCase</h1>
              <p>Jira Test Senaryolarını Saniyeler İçinde Aktarın</p>
            </div>
          </div>
          <div className="mode-badge">
            <Database size={14}/>
            <span>Repo Mode: Active</span>
          </div>
        </div>

        <div className="grid-layout">

          {/* Sidebar */}
          <div className="sidebar">
            <div className="card">
              <label>Repository ID</label>
              <input
                type="number"
                value={repoId}
                onChange={(e) => setRepoId(e.target.value)}
                className="form-input"
                placeholder="Örn: 15"
              />
            </div>

            <div className="card">
              <div style={{display:'flex', justifyContent:'space-between', marginBottom:'8px'}}>
                <label style={{margin:0}}>Hedef Klasör</label>
                <button onClick={() => setShowNewFolder(!showNewFolder)} className="btn-link">
                  <FolderPlus size={14}/> {showNewFolder ? 'İptal' : 'Yeni'}
                </button>
              </div>

              {showNewFolder && (
                <div className="new-folder-area">
                  <input
                    type="text"
                    placeholder="Klasör Adı..."
                    value={newFolderName}
                    onChange={(e) => setNewFolderName(e.target.value)}
                    className="form-input"
                    style={{marginBottom:'8px'}}
                  />
                  <button onClick={handleCreateFolder} className="btn btn-primary btn-sm">
                    Oluştur
                  </button>
                </div>
              )}

              <select
                value={selectedFolder}
                onChange={(e) => setSelectedFolder(e.target.value)}
                className="form-select"
                disabled={folders.length === 0}
              >
                <option value="">
                  {folders.length === 0 ? 'Klasörler yükleniyor...' : 'Klasör Seçin...'}
                </option>
                {folders.map(f => (
                  <option key={f.id} value={f.id}>{f.name}</option>
                ))}
              </select>
            </div>
          </div>

          {/* Content */}
          <div className="content-area">
            <div className="card">
              <label>Jira Task (Örn: PROJ-123)</label>
              <div style={{display:'flex', gap:'10px'}}>
                <input
                  type="text"
                  placeholder="PROJ-123"
                  value={jiraInput}
                  onChange={(e) => setJiraInput(e.target.value)}
                  className="form-input"
                  style={{fontSize:'1.1rem', fontWeight:'600'}}
                />
                <button
                  onClick={handleSync}
                  disabled={loading}
                  className="btn btn-primary"
                  style={{minWidth:'180px'}}
                >
                  {loading ? <Zap className="spin" size={20}/> : <>Quick Sync <ArrowRight size={20}/></>}
                </button>
              </div>
            </div>

            {error && (
              <div className="error-box" style={{background:'#fef2f2', padding:'1rem', borderRadius:'10px', color:'#ef4444', display:'flex', gap:'10px'}}>
                <AlertOctagon />
                <span>{error}</span>
              </div>
            )}

            {syncResult && (
              <div className="result-card">
                <div className="result-header">
                  <div className="success-icon"><Check size={24}/></div>
                  <div>
                    <h3 style={{margin:0, color:'#15803d'}}>Aktarım Başarılı!</h3>
                    <p style={{margin:'5px 0 0 0', fontSize:'0.9rem', color:'#16a34a'}}>
                      {syncResult.taskKey} başarıyla Testmo'ya işlendi.
                    </p>
                  </div>
                </div>

                <div style={{padding:'1.5rem'}}>
                  {syncResult.cases.map((tc, idx) => (
                    <div key={idx} className="case-item">
                      <div style={{display:'flex', alignItems:'center', gap:'10px'}}>
                        <FileText size={18} color="#475569"/>
                        <span style={{fontWeight:600, color:'#334155'}}>{tc.name}</span>
                      </div>
                      <div style={{display:'flex', gap:'5px'}}>
                        <span className="tag tag-created">CREATED</span>
                        {tc.images > 0 && <span className="tag tag-img"><ImageIcon size={12}/> {tc.images} Resim</span>}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
