import { useState, useEffect } from 'react';
import axios from 'axios';
import { RefreshCw, FolderPlus, CheckCircle, XCircle, ArrowRight, Server, Layers } from 'lucide-react';
import './App.css'; // CSS dosyasÄ±nÄ± dahil ediyoruz

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [projectId, setProjectId] = useState(1);
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');

  const [loading, setLoading] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [error, setError] = useState(null);

  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  const fetchFolders = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/folders/${projectId}`);
      const data = response.data.folders || response.data;
      setFolders(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("KlasÃ¶rler alÄ±namadÄ±", err);
    }
  };

  useEffect(() => {
    fetchFolders();
  }, [projectId]);

  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) {
      setError("LÃ¼tfen Jira Linki ve Hedef KlasÃ¶r seÃ§iniz.");
      return;
    }

    setLoading(true);
    setError(null);
    setSyncResult(null);

    try {
      const payload = {
        jira_input: jiraInput,
        folder_id: parseInt(selectedFolder),
        project_id: parseInt(projectId)
      };

      const response = await axios.post(`${API_BASE_URL}/sync`, payload);
      setSyncResult(response.data);
      setJiraInput('');
    } catch (err) {
      setError(err.response?.data?.error || "Senkronizasyon sÄ±rasÄ±nda bir hata oluÅŸtu.");
    } finally {
      setLoading(false);
    }
  };

  const handleCreateFolder = async () => {
    if (!newFolderName) return;

    try {
      const response = await axios.post(`${API_BASE_URL}/folders/${projectId}`, {
        name: newFolderName,
        parent_id: selectedFolder ? parseInt(selectedFolder) : null
      });

      await fetchFolders();
      setNewFolderName('');
      setShowNewFolder(false);
      if(response.data.id) setSelectedFolder(response.data.id);

    } catch (err) {
      setError("KlasÃ¶r oluÅŸturulamadÄ±: " + err.message);
    }
  };

  return (
    <div className="app-container">
      <div className="main-wrapper">

        {/* Header */}
        <div className="header-card">
          <div className="header-left">
            <div className="icon-box bg-primary">
              <RefreshCw color="white" size={24} />
            </div>
            <div>
              <h1>Jira â†” Testmo Sync</h1>
              <p>Test senaryolarÄ±nÄ± ve gÃ¶rselleri otomatik aktarÄ±n</p>
            </div>
          </div>
          <div className="status-badge">
            <Server size={16}/>
            <span>API Status: Ready</span>
          </div>
        </div>

        <div className="grid-layout">

          {/* Sol Panel - Ayarlar */}
          <div className="sidebar">

            <div className="card">
              <label>Project ID</label>
              <input
                type="number"
                value={projectId}
                onChange={(e) => setProjectId(e.target.value)}
                className="form-input"
              />
            </div>

            <div className="card">
              <div className="label-row">
                <label>Hedef KlasÃ¶r</label>
                <button
                  onClick={() => setShowNewFolder(!showNewFolder)}
                  className="btn-link"
                >
                  <FolderPlus size={14}/> Yeni
                </button>
              </div>

              {showNewFolder && (
                <div className="new-folder-box">
                  <input
                    type="text"
                    placeholder="KlasÃ¶r AdÄ±"
                    value={newFolderName}
                    onChange={(e) => setNewFolderName(e.target.value)}
                    className="form-input small-input"
                  />
                  <button onClick={handleCreateFolder} className="btn btn-primary btn-sm width-100">
                    OluÅŸtur
                  </button>
                </div>
              )}

              <select
                value={selectedFolder}
                onChange={(e) => setSelectedFolder(e.target.value)}
                className="form-select"
              >
                <option value="">KlasÃ¶r SeÃ§in...</option>
                {folders.map(f => (
                  <option key={f.id} value={f.id}>{f.name}</option>
                ))}
              </select>
            </div>

          </div>

          {/* SaÄŸ Panel - Ä°ÅŸlemler */}
          <div className="content-area">

            <div className="card">
              <label>Jira Task Linki veya Key</label>
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Ã–rn: PROJ-123"
                  value={jiraInput}
                  onChange={(e) => setJiraInput(e.target.value)}
                  className="form-input"
                />
                <button
                  onClick={handleSync}
                  disabled={loading}
                  className={`btn btn-primary ${loading ? 'disabled' : ''}`}
                >
                  {loading ? <RefreshCw className="spin" size={20} /> : <><span style={{marginRight: '8px'}}>Senkronize Et</span> <ArrowRight size={20} /></>}
                </button>
              </div>
              <p className="helper-text">* "TCxx" formatÄ±ndaki senaryolar Ã§ekilecektir.</p>
            </div>

            {error && (
              <div className="alert alert-error">
                <XCircle size={20} />
                <div>
                  <h3>Ä°ÅŸlem BaÅŸarÄ±sÄ±z</h3>
                  <p>{error}</p>
                </div>
              </div>
            )}

            {syncResult && (
              <div className="result-card">
                <div className="result-header">
                  <CheckCircle color="#16a34a" size={24} />
                  <div>
                    <h3>Senkronizasyon BaÅŸarÄ±lÄ±!</h3>
                    <p>
                      Task: <span className="badge">{syncResult.taskKey}</span> |
                      Case: {syncResult.testCasesCreated} |
                      GÃ¶rsel: {syncResult.imagesUploaded}
                    </p>
                  </div>
                </div>

                <div className="result-body">
                  <h4><Layers size={16}/> AktarÄ±lan Senaryolar</h4>
                  <div className="case-list">
                    {syncResult.cases.map((tc, idx) => (
                      <div key={idx} className="case-item">
                        <div className="case-info">
                          <span className="case-number">{idx + 1}</span>
                          <span className="case-name">{tc.name}</span>
                        </div>
                        <div className="case-meta">
                          <span className={`status-tag status-${tc.status.toLowerCase()}`}>
                            {tc.status}
                          </span>
                          {tc.images > 0 && (
