import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';
import {
  Zap, FolderPlus, Check, Settings,
  FileText, Image as ImageIcon, Loader2,
  PlusCircle, LogOut, Lock, Save, ArrowLeft, ArrowRight
} from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://127.0.0.1:5001/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('qc_token'));
  const [view, setView] = useState('dashboard');

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginLoading, setLoginLoading] = useState(false);

  const [repoId, setRepoId] = useState(1);
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [jiraInput, setJiraInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [foldersLoading, setFoldersLoading] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [showNewFolder, setShowNewFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  const [settingsData, setSettingsData] = useState({
    JIRA_BASE_URL: '', JIRA_EMAIL: '', JIRA_API_TOKEN: '',
    TESTMO_BASE_URL: '', TESTMO_API_KEY: '', FIGMA_ACCESS_TOKEN: ''
  });
  const [settingsLoading, setSettingsLoading] = useState(false);

  useEffect(() => {
    document.title = "QuickCase | Jira Sync";
    if (token) axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  }, [token]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginLoading(true);
    try {
      const res = await axios.post(`${API_BASE_URL}/login`, { username, password });
      localStorage.setItem('qc_token', res.data.access_token);
      setToken(res.data.access_token);
      toast.success("Giriş Başarılı!");
    } catch { toast.error("Giriş başarısız!"); }
    finally { setLoginLoading(false); }
  };

  const handleLogout = () => {
    localStorage.removeItem('qc_token');
    setToken(null);
    setView('dashboard');
    toast('Çıkış yapıldı.');
  };

  const fetchFolders = useCallback(async () => {
    if (!repoId || !token || view !== 'dashboard') return;
    setFoldersLoading(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/folders/${repoId}`);
      setFolders(res.data.folders || []);
    } catch (err) { if(err.response?.status === 401) handleLogout(); }
    finally { setFoldersLoading(false); }
  }, [repoId, token, view]);

  useEffect(() => { fetchFolders(); }, [fetchFolders]);

  const handleSync = async () => {
    if (!jiraInput || !selectedFolder) return toast.error("Alanları doldurun.");
    setLoading(true); setSyncResult(null);
    const tId = toast.loading('QuickCase İşleniyor...');
    try {
      const res = await axios.post(`${API_BASE_URL}/sync`, { jira_input: jiraInput, folder_id: selectedFolder, project_id: repoId });
      setSyncResult(res.data);
      setJiraInput('');
      toast.success("Tamamlandı!", { id: tId });
    } catch (err) { toast.error(err.response?.data?.error || "Hata!", { id: tId }); }
    finally { setLoading(false); }
  };

  const handleCreateFolder = async () => {
    if (!newFolderName) return;
    try {
      const res = await axios.post(`${API_BASE_URL}/folders/${repoId}`, { name: newFolderName, parent_id: selectedFolder || null });
      await fetchFolders();
      if(res.data?.id) setSelectedFolder(res.data.id);
      setNewFolderName(''); setShowNewFolder(false);
      toast.success('Klasör oluşturuldu!');
    } catch (err) { toast.error(err.message); }
  };

  useEffect(() => {
    if (view === 'settings' && token) {
      axios.get(`${API_BASE_URL}/settings`)
        .then(res => setSettingsData(res.data))
        .catch(() => toast.error("Ayarlar yüklenemedi"));
    }
  }, [view, token]);

  const saveSettings = async () => {
    setSettingsLoading(true);
    try {
      await axios.post(`${API_BASE_URL}/settings`, settingsData);
      toast.success("Ayarlar Kaydedildi!");
      setTimeout(() => setView('dashboard'), 1000);
    } catch { toast.error("Kaydedilemedi!"); }
    finally { setSettingsLoading(false); }
  };

  if (!token) return (
    <div className="app-container login-container">
      <Toaster position="top-center"/>
      <div className="login-card">
        <div className="login-header">
          <div className="logo-box" style={{width:64, height:64, margin:'0 auto 1rem'}}><Zap size={32}/></div>
          <h1>QuickCase</h1><p>Yönetici Girişi</p>
        </div>
        <form onSubmit={handleLogin}>
          <div className="form-group"><label>Kullanıcı Adı</label><input className="form-input" value={username} onChange={e=>setUsername(e.target.value)} placeholder="admin"/></div>
          <div className="form-group"><label>Şifre</label><input type="password" className="form-input" value={password} onChange={e=>setPassword(e.target.value)} placeholder="••••••"/></div>
          <button type="submit" className="btn btn-primary" disabled={loginLoading}>{loginLoading ? <Loader2 className="spinner"/> : <><Lock size={18}/> Giriş Yap</>}</button>
        </form>
      </div>
    </div>
  );

  return (
    <div className="app-container">
      <Toaster position="top-center"/>
      <div className="main-wrapper">

        <div className="header-card">
          <div className="header-brand">
            <div className="logo-box"><Zap size={28}/></div>
            <div className="brand-text"><h1>QuickCase</h1><p>Otomatik Test Senaryo Transferi</p></div>
          </div>
          <div style={{display:'flex', gap:'10px'}}>
            {view === 'dashboard' && <button onClick={() => setView('settings')} className="btn btn-text" title="Ayarlar"><Settings size={20}/> Ayarlar</button>}
            <button onClick={handleLogout} className="btn btn-text text-red"><LogOut size={20}/></button>
          </div>
        </div>

        {view === 'settings' ? (
          <div className="settings-view card" style={{animation: 'fadeIn 0.3s ease-out'}}>
             <div style={{display:'flex', alignItems:'center', gap:'15px', marginBottom:'25px', borderBottom:'1px solid #e2e8f0', paddingBottom:'15px'}}>
                <button onClick={()=>setView('dashboard')} className="btn" style={{background:'#f1f5f9', padding:'8px 12px', color:'#64748b'}}><ArrowLeft size={20}/> <span style={{marginLeft:6}}>Geri</span></button>
                <div><h2 style={{margin:0, fontSize:'1.25rem', color:'#1e293b'}}>QuickCase Ayarları</h2><p style={{margin:0, fontSize:'0.85rem', color:'#94a3b8'}}>API ve Entegrasyon Yapılandırması</p></div>
             </div>
             <div className="settings-grid">
                {Object.keys(settingsData).map(key => (
                   <div key={key} className="form-group">
                      <label>{key.replace(/_/g, ' ')}</label>
                      <input className="form-input" type={key.includes('TOKEN') || key.includes('KEY') ? "password" : "text"} value={settingsData[key]} onChange={e => setSettingsData({...settingsData, [key]: e.target.value})} placeholder={`${key}...`}/>
                   </div>
                ))}
             </div>
             <div style={{display:'flex', justifyContent:'flex-end', gap:'10px', marginTop:'10px'}}>
                <button onClick={()=>setView('dashboard')} className="btn btn-text" style={{color:'#64748b'}}>İptal</button>
                <button onClick={saveSettings} className="btn btn-primary" disabled={settingsLoading} style={{width:'140px'}}>{settingsLoading ? <Loader2 className="spinner" size={20}/> : <><Save size={18}/> Kaydet</>}</button>
             </div>
          </div>
        ) : (
          <div className="grid-layout">
            <div className="sidebar">
              <div className="card">
                <div className="form-group"><label>Proje ID (Testmo)</label><input type="number" className="form-input" value={repoId} onChange={e=>setRepoId(e.target.value)}/></div>
              </div>
              <div className="card">
                <div className="form-group">
                  <label style={{display:'flex', justifyContent:'space-between'}}>Hedef Klasör <button onClick={()=>setShowNewFolder(!showNewFolder)} className="btn-text"><FolderPlus size={16}/> Yeni</button></label>
                  {showNewFolder && <div className="new-folder-wrapper"><input className="form-input" placeholder="Ad..." value={newFolderName} onChange={e=>setNewFolderName(e.target.value)}/><button onClick={handleCreateFolder} className="btn btn-success"><PlusCircle size={18}/> Oluştur</button></div>}
                  <div className="input-container"><select className="form-select" value={selectedFolder} onChange={e=>setSelectedFolder(e.target.value)} disabled={foldersLoading}><option value="">{foldersLoading ? 'Yükleniyor...' : 'Seçiniz...'}</option>{folders.map(f=><option key={f.id} value={f.id}>{f.name}</option>)}</select></div>
                </div>
              </div>
            </div>
            <div className="content-area">
              <div className="card">
                <label className="form-label" style={{fontSize:'1.1rem'}}>Jira Task</label>
                <div style={{display:'flex', gap:'12px', marginTop:'1rem'}}>
                  <div style={{flex:1}}><input className="form-input" placeholder="Örn: PROJ-123" value={jiraInput} onChange={e=>setJiraInput(e.target.value)} style={{padding:'1rem', fontSize:'1.1rem', fontWeight:'600'}}/></div>
                  <button onClick={handleSync} disabled={loading} className="btn btn-primary" style={{width:'200px'}}>{loading ? <><Loader2 className="spinner" size={20}/> İşleniyor...</> : <><Zap size={20}/> Senkronize Et <ArrowRight size={20}/></>}</button>
                </div>
              </div>
              {syncResult && (
                <div className="result-card">
                  <div className="result-header"><div className="success-icon-box"><Check size={28} strokeWidth={3}/></div><div><h3 className="result-title">İşlem Tamamlandı!</h3><div className="result-meta"><span className="meta-tag">Task: {syncResult.taskKey}</span><span className="meta-tag">Case: {syncResult.testCasesCreated}</span><span className="meta-tag">Görsel: {syncResult.imagesUploaded}</span></div></div></div>
                  <div className="case-list">{syncResult.cases.map((tc, idx) => (<div key={idx} className="case-item"><div className="case-info"><FileText size={20} className="text-gray-400"/><span className="case-name">{tc.name}</span></div><div style={{display:'flex', alignItems:'center', gap:'8px'}}><span className="status-badge-success">CREATED</span>{tc.images > 0 && <span className="img-badge"><ImageIcon size={14}/> {tc.images}</span>}</div></div>))}</div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
